const useAuth = () => {
  const cookies = document.cookie;

  const cookieArray = cookies.split("; ");

  let cookieFlag = false;

  cookieArray.forEach((elem) => {
    if (elem.split("=")[0] === "shop") {
      cookieFlag = true;
    }
  });

  return cookieFlag;
};

export default useAuth;
