import { createSlice } from "@reduxjs/toolkit";

import {
  getAllnewssThunk
} from "../Data/newsThunk";

const initialState = {
  newsList: [],
  loading: {
    newss: false,

  },


};

const newsSlice = createSlice({
  name: "newsSlice",
  initialState,
  reducers: {},
  extraReducers: {
    [getAllnewssThunk.pending]: (state = initialState, _) => {
      state.loading.newss = true;

    },
    [getAllnewssThunk.fulfilled]: (state = initialState, action) => {
      state.newsList = action.payload.newss.map((item) => ({
        ...item,
        count: 1,
      }));


    },


  },
});

export default newsSlice.reducer;
