import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  getAllProductsThunk,
  getAllCategoryThunk,
} from "../../Data/productThunk";
import useAuth from "../../helpers/useAuth";
import usePagination from "../../helpers/usePagination";
import Header from "../components/Header";
import ProductsList from "../layouts/ProductsList";
import Slider from "../components/Slider";
import ImageSvg from "../components/ImageSVG";

import "../components/component.css";
import SliderSkeleton from "../components/Slider/SliderSkeleton";

const Main = () => {
  const { categories, loading, pagination } = useSelector(
    (state) => state.productSlice
  );
  const dispatch = useDispatch();

  const isAuth = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuth) {
      navigate("/main");
      dispatch(getAllProductsThunk({ limit: 9, page: 0 }));
      dispatch(getAllCategoryThunk());
    } else {
      navigate("/");
    }
  }, []);

  return (
    <div className="mainPage">
      <Header />
      {!loading.category ? (
        <Slider
          items={categories.map((text, i) => (
            <div className="slider_item" key={i}>
              <button className="categoryPanelComponent_item">
                <div className="categoryPanelComponent_item-circle">
                  <ImageSvg
                    config={{
                      icon: "Bags",
                    }}
                  />
                </div>
                <p className="categoryPanelComponent_item-title">{text}</p>
              </button>
            </div>
          ))}
        />
      ) : (
        <SliderSkeleton />
      )}
      <div className="productList">
        <ProductsList />
      </div>
    </div>
  );
};

export default Main;
