import { createAsyncThunk } from "@reduxjs/toolkit";
import { setCookie } from "../helpers/setCookie";

const BASE_URL = {
  all_products: "https://dummyjson.com/products",
  auth: "https://dummyjson.com/auth/login",
};

export const getAllProductsThunk = createAsyncThunk(
  "productSlice/getAllProductsThunk",
  async function ({ limit, page }) {
    try {
      const response = await fetch(
        BASE_URL.all_products + "?limit=" + limit + "&skip=" + page
      );

      const data = await response.json();

      return data;
    } catch (error) {}
  }
);

export const getAllCategoryThunk = createAsyncThunk(
  "productSlice/getAllCategoryThunk",
  async function () {
    try {
      const response = await fetch(BASE_URL.all_products + "/categories");

      const data = await response.json();

      return data;
    } catch (error) {}
  }
);

export const getUserAuthThunk = createAsyncThunk(
  "userSlice/getUserAuthThunk",
  async function (value, { rejectWithValue }) {
    try {
      const response = await fetch(BASE_URL.auth, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(value),
      });

      if (!response.ok) {
        throw new Error("Вы неверно указали логин или пароль!!!");
      }

      const data = await response.json();

      setCookie({ username: data.username, password: value.password });

      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
