import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  getAllProductsThunk,
  getAllCategoryThunk,
  getProductsOfCategoryThunk,
} from "../../Data/productThunk";
import useAuth from "../../helpers/useAuth";
import Header from "../components/Header";
import ProductsList from "../layouts/ProductsList";
import Slider from "../components/Slider";
import ImageSvg from "../components/ImageSVG";
import "../components/component.css";
import SliderSkeleton from "../components/Slider/SliderSkeleton";
import Pagination from "../components/Pagination";
import PaginationSkeleton from "../components/Pagination/PaginationSkeleton";
import usePagination from "../../helpers/usePagination";

const Main = () => {
  const [page, setPage] = useState(1);
  const { categories, loading, pagination } = useSelector(
    (state) => state.productSlice
  );
  const dispatch = useDispatch();

  const isAuth = useAuth();
  const navigate = useNavigate();

  const { countPaginationBtns, skip, isPagination } = usePagination({
    ...pagination,
    page,
  });

  const numberPagination = (page) => {
    setPage(page);
  };

  useEffect(() => {
    dispatch(getAllProductsThunk({ limit: 9, page: skip }));
  }, [page]);

  useEffect(() => {
    if (isAuth) {
      navigate("/main");

      dispatch(getAllCategoryThunk());
    } else {
      navigate("/");
    }
  }, []);

  return (
    <div className="mainPage">
      <Header />

      <div className="productList">
        <ProductsList />
      </div>


    </div>
  );
};

export default Main;
