import React from "react";
import { useDispatch } from "react-redux";
import { removeToCart } from "../../../Business/cartSlice";
import ImageSvg from "../ImageSVG";
import "./CartItem.css";

const CartItem = ({ item }) => {
  const dispatch = useDispatch();
  return (
    <div className="cartItem">
      <div className="cartItem_img"></div>
      <div className="">
        <p className="cartItem_title">{item.title}</p>
        <p className="cartItem_category">{item.category}</p>
      </div>
      <div className="cartItem_price">
        <p>{item.count}</p>
        <span>x</span>
        <p>{item.price}</p>
      </div>
      <p className="cartItem_totalPrice">100</p>
      <button
        className="cartItem_removeBtn"
        onClick={() => dispatch(removeToCart(item.id))}
      >
        <ImageSvg
          config={{
            icon: "Remove",
            fill: "gray",
            stroke: "none",
            size: 16,
          }}
        />
      </button>
    </div>
  );
};

export default CartItem;
