import React from "react";
import ImageSvg from "../ImageSVG";

import "./Pagination.css";

const Pagination = ({ config }) => {
  const handlePagination = (page) => {
    config.numberPagination(page);
  };

  return (
    <>
      {config.isPagination ? (
        <div className="paginationBtns">
          <button className="paginationBtn" onClick={() => handlePagination(1)}>
            <ImageSvg
              config={{
                icon: "DoubleArrowPrev",
                fill: "none",
                stroke: "#000000",
                size: 16,
                style: true,
              }}
            />
          </button>
          <button
            className="paginationBtn"
            onClick={() => handlePagination(config.page - 1)}
            disabled={config.page > 1 ? false : true}
          >
            <ImageSvg
              config={{
                icon: "ArrowPrev",
                fill: "#00000",
                stroke: "none",
                size: 16,
                style: true,
              }}
            />
          </button>
          {/* {Array(config.countPaginationBtns)
        .fill(1)
        .map((_, i) => (
          <button
            key={i + 1}
            className="paginationBtn"
            onClick={() => handlePagination(i + 1)}
          >
            {i + 1}
          </button>
        ))} */}
          <button className="paginationBtn">{config.page}</button>
          <button
            className="paginationBtn"
            onClick={() => handlePagination(config.page + 1)}
          >
            <ImageSvg
              config={{
                icon: "ArrowNext",
                fill: "#00000",
                stroke: "none",
                size: 16,
                style: true,
              }}
            />
          </button>
          <button
            className="paginationBtn"
            onClick={() => handlePagination(config.countPaginationBtns)}
          >
            <ImageSvg
              config={{
                icon: "DoubleArrowNext",
                fill: "none",
                stroke: "#000000",
                size: 16,
                style: true,
              }}
            />
          </button>
        </div>
      ) : null}
    </>
  );
};

export default Pagination;
