const usePagination = (pagination) => {
  let isPagination = true;

  const countPaginationBtns = Math.ceil(pagination.total / pagination.limit);

  const skip = pagination.limit * (pagination.page - 1);

  if (pagination.limit === pagination.total) {
    isPagination = false;
  }

  return { countPaginationBtns, skip, isPagination };
};

export default usePagination;
