import React, { useState, useEffect } from "react";
import { getUserAuthThunk } from "../../Data/productThunk";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import useAuth from "../../helpers/useAuth";
import { useNavigate } from "react-router-dom";

import "./pages.css";

const Auth = () => {
  const [user, setUser] = useState({
    username: "",
    password: "",
  });
  const dispatch = useDispatch();
  const { error, loading, message } = useSelector((state) => state.userSlice);
  const isAuth = useAuth();
  const navigate = useNavigate();

  const handleChange = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(getUserAuthThunk(user));
    setUser({
      username: "",
      password: "",
    });
  };

  useEffect(() => {
    if (isAuth) {
      navigate("/main");
    } else {
      navigate("/");
    }
  }, []);

  return (
    <div className="authPage">
      {loading ? (
        <p>loading...</p>
      ) : (
        <form className="formAuthPage" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Введите имя пользователя:"
            name="username"
            onChange={handleChange}
            value={user.username}
            className={error ? "error_border" : "auth_input_border"}
          />
          <input
            type="password"
            placeholder="Введите пароль пользователя:"
            name="password"
            onChange={handleChange}
            value={user.password}
            className={error ? "error_border" : "auth_input_border"}
          />
          <input type="submit" value="Вход" />
          {error && <p className="auth_error_text">{message}</p>}
        </form>
      )}
    </div>
  );
};

export default Auth;
