import { useEffect, useState, Children, cloneElement } from "react";
import "./Slider.css";
import ImageSvg from "../ImageSVG";

const PAGE_WIDTH = 600;
const step = 100;

const Slider = ({ items }) => {
  const [pages, setPages] = useState([]);
  const [offset, setOffset] = useState(0);

  const handleLeftArrowClick = () => {
    setOffset((currentOffset) => {
      const newOffset = currentOffset + step;
      return Math.min(newOffset, 0);
    });
  };
  const handleRightArrowClick = () => {
    setOffset((currentOffset) => {
      // const maxOffset = -(pages.length * step - PAGE_WIDTH);
      // return Math.min(newOffset, sliders_width_minus);
      const newOffset = currentOffset - step;
      return Math.max(newOffset, -1400);
    });
  };

  useEffect(() => {
    setPages(
      Children.map(items, (child) => {
        return cloneElement(child, {
          style: {
            minWidth: `${PAGE_WIDTH}px`,
            maxWidth: `${PAGE_WIDTH}px`,
            height: "100%",
          },
        });
      })
    );
  }, []);

  return (
    <div className="main-container">
      <span onClick={handleLeftArrowClick}>
        <ImageSvg
          config={{
            icon: "ArrowPrev",
            stroke: "#c2c2c2",
            fill: "#c2c2c2",
          }}
        />
      </span>
      <div className="window">
        <div
          className="all-pages-container"
          style={{
            transform: `translateX(${offset}px)`,
          }}
        >
          {items}
        </div>
      </div>
      <span onClick={handleRightArrowClick}>
        <ImageSvg
          config={{
            icon: "ArrowNext",
            stroke: "#c2c2c2",
            fill: "#c2c2c2",
          }}
        />
      </span>
    </div>
  );
};

export default Slider;
