import React, { useState } from "react";
import ImageSvg from "../ImageSVG";
import { addToCart } from "../../../Business/cartSlice";
import { useDispatch } from "react-redux";
import Modal from "../Modal";

import "./ProductItem.css";

const ProductItem = ({ product }) => {
  const [isModal, setIsModal] = useState(false);
  const count_star = Math.round(product.rating);

  const dispatch = useDispatch();

  const handleToCart = () => {
    dispatch(addToCart(product));
  };

  const hanldeChangeModal = (flag) => {
    setIsModal(flag);
  };

  return (
    <>
      <div className="productList_item">
        <div className="productList_bg" onClick={() => hanldeChangeModal(true)}>
          <div
            className="productList_add"
            onClick={(e) => {
              handleToCart();
              e.stopPropagation();
            }}
          >
            <ImageSvg
              config={{
                icon: "Plus",
                fill: "none",
                stroke: "#000000",
                size: 24,
              }}
            />
          </div>
        </div>
        <div className="productList_price-rait">
          <h3 className="productList_price">$ {product.price}</h3>
          <div className="productList_rait">
            {Array(5)
              .fill("")
              .map((_, i) => (
                <ImageSvg
                  key={i}
                  config={{
                    icon: "Star",
                    fill: `${i + 1 > count_star ? "#eaeaea" : "#64ECC7"}`,
                    stroke: `${i + 1 > count_star ? "#eaeaea" : "#64ECC7"}`,
                    size: 16,
                  }}
                />
              ))}
          </div>
        </div>
        <p className="productList_title">
          {product.title.length > 25
            ? `${product.title.slice(0, 22)}...`
            : product.title}
        </p>
      </div>
      {isModal && (
        <Modal close={hanldeChangeModal}>
          {/* <img src={product.thumbnail} alt="" /> */}
          <div className="modal_info">
            <p>{product.title}</p>
            <p>{product.price}</p>
          </div>
          <p>{product.description}</p>
        </Modal>
      )}
    </>
  );
};

export default ProductItem;
