import { createSlice } from "@reduxjs/toolkit";
import { getUserAuthThunk } from "../Data/productThunk";

const initialState = {
  user: {},
  loading: false,
  error: null,
  message: "",
  isAuth: false,
  path: "/",
};

const userSlice = createSlice({
  name: "userSlice",
  initialState,
  reducers: {
    isAuth: (state = initialState, action) => {
      state.isAuth = action.payload.cookieFlag;
      state.path = action.payload.path;
    },
  },
  extraReducers: {
    //Auth
    [getUserAuthThunk.pending]: (state = initialState, _) => {
      state.loading = true;
      state.error = null;
    },
    [getUserAuthThunk.fulfilled]: (state = initialState, action) => {
      state.user = action.payload;
      state.loading = false;
      state.error = null;
    },
    [getUserAuthThunk.rejected]: (state = initialState, action) => {
      state.error = true;
      state.loading = false;
      state.message = action.payload;
    },
  },
});

export const { isAuth } = userSlice.actions;

export default userSlice.reducer;
